mapper.select:
  		select * from msg_push
  	
  		
  			
	  			and	userId in (1,#{userId})
  			
  			
	  			and	userId = 1
  			
  			
		  		
		  			and	msgType = #{msgType} and userId = #{userId}
		  		
  			
  		
  	
  	 	ORDER BY releaseDate DESC
  	 	
  	 	limit #{from},#{pageSize}
